import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { Form, FormGroup, Label, Input } from 'reactstrap';
import Button from 'react-bootstrap/Button'

export default class imageUpload  extends Component{
    constructor(props) {
        super(props);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePicture = this.onChangePicture.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            username : '',
            picture: ''
        }
    }

    onChangeUsername(e){
        this.setState({
        username: e.target.value
        })
    }

    onChangePicture(e){
        this.setState({picture:e.target.files});
    }
    onSubmit(e){
        e.preventDefault();

        const image = {
            username: this.state.username,
            picture: this.state.picture
        }

        console.log(image);

        axios.post('http://localhost:5000/images/add',image)
            .then(res => console.log(res.data));
        this.setState({
            username: '',
            picture: ''
        })
        //window.location = '/usercreated';
    }

    render() {
        return (
            <form onSubmit={this.onFormSubmit}>
              <h1>File Upload</h1>
              <input type="picture" className="custom-file-input" name="myImage" onChange= {this.onChangePicture} />
              {console.log(this.state.file)}
              <h1>Username</h1>
              <input  type="text"
                    required
                    className="form-control"
                    value={this.state.username}
                    onChange={this.onChangeUsername}
                />
              <button className="upload-button" type="submit">Upload to DB</button>
          </form>
        )
    }
}
